cd /tmp && cat > chatroom-bot.js << 'EOF'
const { chromium } = require('playwright');

(async () => {
  const browser = await chromium.launch({ headless: true });
  const page = await browser.newPage();

  await page.goto('https://chatroom-6o4.pages.dev');
  await page.waitForTimeout(2000);

  // Find input and type hi
  const input = await page.locator('input[placeholder="Type a message..."]').first();
  await input.fill('Hi from ÈæçËù¶‰ªî! ü¶û');

  // Click send
  const sendBtn = await page.locator('button.send-btn').first();
  await sendBtn.click();

  await page.waitForTimeout(1000);

  // Screenshot to verify
  await page.screenshot({ path: '/data/workspace/chatroom-result.png' });

  await browser.close();
  console.log('Done! Screenshot saved.');
})();
EOF

cd /tmp && cat > chatroom-bot2.js << 'EOF'
const { chromium } = require('playwright');

(async () => {
  const browser = await chromium.launch({ headless: true });
  const page = await browser.newPage();

  await page.goto('https://chatroom-6o4.pages.dev');
  await page.waitForTimeout(3000);

  // Find input and type a simple message
  const input = await page.locator('input[placeholder="Type a message..."]').first();
  await input.fill('Hello');

  // Click send
  const sendBtn = await page.locator('button.send-btn').first();
  await sendBtn.click();

  // Wait longer to see if there's any reply
  await page.waitForTimeout(5000);

  // Screenshot to check for replies
  await page.screenshot({ path: '/data/workspace/chatroom-test2.png' });

  // Also get page content to see messages
  const messages = await page.locator('.messages').textContent();
  console.log('Messages found:', messages);

  await browser.close();
  console.log('Done!');
})();
EOF
node chatroom-bot2.js 2>&1

